mapper.insert:
insert into comment(cm_num, cm_content, cm_me_id, cm_po_num, cm_ori_num) select ifnull(max(cm_num)+ 1,1) , #{comment.cm_content}, #{comment.cm_me_id}, #{comment.cm_po_num},
ifnull(max(cm_num)+ 1,1) #{comment.cm_ori_num}
from comment

mapper.select:
select * from comment where cm_po_num = #{cri.search}
order by cm_ori_num desc, cm_num asc
limit #{cri.pageStart}, #{cri.perPageNum}
